using AEAssist;
using AEAssist.CombatRoutine;
using AEAssist.CombatRoutine.Trigger;
using AEAssist.CombatRoutine.Trigger.Node;
using AEAssist.Extension;
using AEAssist.Helper;



namespace YouShu.爆发药检测
{
    public class WaitForPotionAndUse3 : ITriggerScript
    {
        private const int InvalidItemId = 0;
        private const int FastCheckIntervalMs = 100;
        private const int SlowCheckIntervalMs = 1000;
        private const int MaxWaitTimeMs = 10000;
        private const int MaxRetryCount = 3;

        private int isWaiting = 0;
        private int useRequested = 0;
        private uint currentPotionId = 0;
        private long startTime = 0;
        private long lastCheckTime = 0;

        // 使用Jobs枚举替代int
        private Jobs? cachedJob = null;
        private PotionSetting? cachedSetting = null;
        private uint cachedPotionId = InvalidItemId;

        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {
            // 如果不在等待状态，初始化药水检测
            if (Interlocked.CompareExchange(ref isWaiting, 1, 0) == 0)
            {
                try
                {
                    // 基础检查
                    if (Core.Me == null)
                    {
                        LogHelper.Error("WaitForPotionAndUse: Core.Me is null");
                        Interlocked.Exchange(ref isWaiting, 0);
                        return false;
                    }

                    // 获取当前职业 - 使用正确的Jobs枚举类型
                    var currentJob = Core.Me.CurrentJob();

                    // 只有在职业变化时才重新获取设置
                    if (!cachedJob.HasValue || cachedJob.Value != currentJob)
                    {
                        cachedJob = currentJob;
                        cachedSetting = SettingMgr.GetSetting<PotionSetting>();

                        if (cachedSetting == null)
                        {
                            LogHelper.Error($"WaitForPotionAndUse: PotionSetting not found for job {currentJob}");
                            Interlocked.Exchange(ref isWaiting, 0);
                            return false;
                        }

                        cachedPotionId = cachedSetting.GetPotionId(currentJob);
                        if (cachedPotionId <= InvalidItemId)
                        {
                            LogHelper.Info($"WaitForPotionAndUse: Invalid potion ID {cachedPotionId} for job {currentJob}");
                            Interlocked.Exchange(ref isWaiting, 0);
                            return false;
                        }
                    }

                    // 初始化等待状态
                    currentPotionId = cachedPotionId;
                    startTime = Environment.TickCount64;
                    lastCheckTime = startTime;

                    LogHelper.Info($"WaitForPotionAndUse: Starting to wait for potion {currentPotionId} cooldown...");
                }
                catch (Exception ex)
                {
                    LogHelper.Error($"WaitForPotionAndUse: Exception during initialization: {ex.Message}");
                    Interlocked.Exchange(ref isWaiting, 0);
                    return false;
                }
            }

            // 检查是否超时
            var elapsedTime = Environment.TickCount64 - startTime;
            if (elapsedTime > MaxWaitTimeMs)
            {
                LogHelper.Info($"WaitForPotionAndUse: Potion {currentPotionId} wait timed out after {elapsedTime}ms");
                ResetState();
                return false;
            }

            // 控制检查频率，减少不必要的调用
            var currentTime = Environment.TickCount64;
            var cooldown = Core.Me.GetItemCoolDown(currentPotionId);
            var cooldownMs = cooldown.TotalMilliseconds;
            var checkInterval = cooldownMs < 2000 ? FastCheckIntervalMs : SlowCheckIntervalMs;

            if (currentTime - lastCheckTime < checkInterval)
            {
                return false; // 未达到检查间隔，直接返回
            }

            lastCheckTime = currentTime;

            // 检查药水冷却状态
            if (cooldownMs <= 0)
            {
                LogHelper.Info($"WaitForPotionAndUse: Potion {currentPotionId} is ready! Requesting use...");

                // 只请求使用，不等待结果
                if (Interlocked.CompareExchange(ref useRequested, 1, 0) == 0)
                {
                    // 启动异步任务但不等待
                    _ = UsePotionWithRetryAsync(currentPotionId, MaxRetryCount);
                }

                // 返回true表示条件满足，不关心实际使用结果
                ResetState();
                return true;
            }

            // 继续等待
            return false;
        }

        private async Task UsePotionWithRetryAsync(uint potionId, int maxRetries)
        {
            try
            {
                int retryCount = 0;
                bool useResult = false;

                while (retryCount < maxRetries && !useResult)
                {
                    try
                    {
                        useResult = await ItemHelper.UseItem(potionId, false);

                        if (useResult)
                        {
                            LogHelper.Info($"WaitForPotionAndUse: Successfully used potion {potionId} after {retryCount} retries");
                            return;
                        }
                        else
                        {
                            retryCount++;
                            LogHelper.Info($"WaitForPotionAndUse: Failed to use potion {potionId}, retry {retryCount}/{maxRetries}");

                            // 短暂延迟后重试
                            await Task.Delay(200);
                        }
                    }
                    catch (Exception ex)
                    {
                        retryCount++;
                        LogHelper.Error($"WaitForPotionAndUse: Exception while using potion (retry {retryCount}/{maxRetries}): {ex.Message}");

                        // 发生异常时也进行延迟
                        await Task.Delay(500);
                    }
                }

                LogHelper.Error($"WaitForPotionAndUse: Failed to use potion {potionId} after {maxRetries} attempts");
            }
            catch (Exception ex)
            {
                LogHelper.Error($"WaitForPotionAndUse: Critical exception in UsePotionWithRetryAsync: {ex.Message}");
            }
        }

        private void ResetState()
        {
            Interlocked.Exchange(ref isWaiting, 0);
            Interlocked.Exchange(ref useRequested, 0);
            Interlocked.Exchange(ref currentPotionId, InvalidItemId);
            Interlocked.Exchange(ref startTime, 0);
            Interlocked.Exchange(ref lastCheckTime, 0);
        }
    }
}

