namespace Nagomi.GNB.Opener;

public class M6S : IOpener
{
    public int StartCheck()
    {
        return 0;
    }

    public int StopCheck(int index)
    {
        return -1;
    }

    public List<Action<Slot>> Sequence { get; } = new()
    {
        Step0,
        Step1,

    };

    public Action CompeltedAction { get; set; }


   // public int StepCount => 9;


    private static void Step0(Slot slot)
    {
        slot.Add(new Spell(Spells.利刃斩, SpellTargetType.Target));
        slot.Add(new Spell(Spells.血壤, SpellTargetType.Target));
        slot.Add(new SlotAction(Spell.CreatePotion()));
    }

    private static void Step1(Slot slot)
    {
        slot.Add(new Spell(Spells.残暴弹, SpellTargetType.Target));
        slot.Add(new Spell(Spells.爆发击, SpellTargetType.Target));
        slot.Add(new Spell(Spells.无情, SpellTargetType.Self));
        slot.AddDelaySpell(800,new Spell(Spells.超音速, SpellTargetType.Target));
        slot.Add(new Spell(Spells.雪仇, SpellTargetType.Self));
    }






    public uint Level { get; } = 100;


    public void InitCountDown(CountDownHandler countDownHandler)//倒数行为
    {
        LogHelper.Print("M6S起手");//打印信息,可选
        if(GNBSettings.Instance.起手语音播报){countDownHandler.AddAction(10660, () => {AudioPlayer.PlayAudioFile( "敌军还有10秒到达战场请做好准备.wav"); });}
            countDownHandler.AddAction(5660, Spells.刚玉之心);
            if (Core.Me.HasAura(Buffs.王室亲卫)&&GNBSettings.Instance.ST)
                countDownHandler.AddAction(4500, Spells.关盾姿);
            if (!Core.Me.HasAura(Buffs.王室亲卫)&&!GNBSettings.Instance.ST)
                countDownHandler.AddAction(4000, Spells.盾姿);
            if(GNBSettings.Instance.起手语音播报){countDownHandler.AddAction(900, () => {AudioPlayer.PlayAudioFile( "全军出击.wav"); });}
            if (QT.QTGET(QTKey.突进起手) &&!QT.QTGET(QTKey.停手)){ countDownHandler.AddAction(660, Spells.弹道, SpellTargetType.Target); }


    }
}
