 using AEAssist;
using System.Numerics;
using AEAssist.CombatRoutine.Trigger;
using AEAssist.CombatRoutine.Trigger.Node;
using AEAssist.Helper;
using AEAssist.Extension;
using AEAssist.MemoryApi;
using AEAssist.CombatRoutine.Trigger.TriggerCond;
using AEAssist.CombatRoutine.Module;
using AEAssist.Avoid;
using System.Linq;
using System.Collections.Generic;

namespace AutoRaidHelper.Triggers.UWU
{
    public class 危险区检测合并 : ITriggerScript
    {
        // 热风处理相关变量
        private static readonly Random random = new Random();
        private List<uint> activeHeatWindShapeIds = new List<uint>();
        private DateTime lastHeatWindUpdate = DateTime.MinValue;
        private const int HEAT_WIND_UPDATE_INTERVAL = 50; // 50毫秒更新一次

        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {
            var mapBaseName = "w1fz";
            uint randomUint = (uint)random.Next(1, int.MaxValue);

            var now = DateTime.Now;
            if ((now - lastHeatWindUpdate).TotalMilliseconds >= HEAT_WIND_UPDATE_INTERVAL)
            {
                // 清除之前的危险区ID列表
                activeHeatWindShapeIds.Clear();

                // 查找所有带有热风状态的目标
                var targets = PartyHelper.Party;
                foreach (var target in targets)
                {
                    if (target.HasAura(1578)) // 热风状态
                    {
                        // 为每个热风目标生成唯一的危险区ID
                        uint heatWindShapeId = (uint)random.Next(1, int.MaxValue);
                        activeHeatWindShapeIds.Add(heatWindShapeId);

                        if (AvoidManager.Instance.GetMap(mapBaseName, out var heatWindMap))
                        {
                            // 统一使用15半径的危险区
                            heatWindMap.AddDangerShape(heatWindShapeId, new DangerShape(0, 50, new CircleShape()
                            {
                                Origin = target.Position,
                                Radius = 15f,
                            }));
                        }
                    }
                }

                // 更新最后更新时间
                lastHeatWindUpdate = now;
            }

            if (condParams is EnemyCastSpellCondParams enemyCastSpell)
            {
                if (AvoidManager.Instance.GetMap(mapBaseName, out var map))
                {
                    // 风神技能检测
                    switch (enemyCastSpell.SpellId)
                    {
                        case 11073: // 大龙卷风钢铁
                            LogHelper.Print("大龙卷风钢铁出现");
                            map.AddDangerShape(randomUint, new DangerShape(0, 8000, new CircleShape()
                            {
                                Origin = enemyCastSpell.CastPos,
                                Radius = 8f,
                            }));
                            break;

                        case 11090: // 台风眼月环
                            LogHelper.Print("台风眼月环");
                            map.AddDangerShape(randomUint, new DangerShape(0, 8000, new DonutShape()
                            {
                                Origin = new Vector3(100, 0, 100),
                                SafeRadius = 11.5f,
                            }));
                            break;

                        case 11086: // 邪轮旋风钢铁
                            LogHelper.Print("邪轮旋风钢铁出现");
                            map.AddDangerShape(randomUint, new DangerShape(0, 3000, new CircleShape()
                            {
                                Origin = enemyCastSpell.CastPos,
                                Radius = 9f,
                            }));
                            break;

                        case 11097: // 地火技能1
                        case 11098: // 地火技能2
                            Vector3 地火pos = enemyCastSpell.CastPos;
                            LogHelper.Print($"地火出现位置：{地火pos}");
                            Share.TrustDebugPoint.Add(地火pos);
                            map.AddDangerShape(randomUint, new DangerShape(0, 10000, new CircleShape()
                            {
                                Origin = 地火pos,
                                Radius = 8f,
                            }));
                            break;
                    }
                }
            }


            return false;
        }
    }
}

using AEAssist;
using System.Numerics;
using AEAssist.ACT;
using AEAssist.Avoid;
using AEAssist.CombatRoutine.Module;
using AEAssist.CombatRoutine.Trigger;
using AEAssist.CombatRoutine.Trigger.TriggerAction;
using AEAssist.CombatRoutine.Trigger.Node;
using AEAssist.CombatRoutine.Trigger.TriggerCond;
using AEAssist.DynamicComplie;
using AEAssist.Helper;
using AEAssist.MemoryApi;

namespace AutoRaidHelper.Triggers.UWU;

public class 躲羽毛ae : ITriggerScript
{
    // 羽毛危险区相关变量
    private static readonly Random random = new Random();
    private bool hasTriggeredThisRound = false;
    private long lastTriggerTime = 0;
    private const long COOLDOWN_MS = 1500; // 冷却时间：1.5秒内不重复执行
    private const float FEATHER_RADIUS = 3f; // 羽毛危险区半径
    private const int FEATHER_DURATION = 2500; // 羽毛危险区持续时间（毫秒）

    /// <summary>
    /// 为所有队伍成员添加羽毛危险区
    /// </summary>
    private void AddFeatherDangerZones()
    {
        var mapBaseName = "w1fz";

        if (!AvoidManager.Instance.GetMap(mapBaseName, out var map))
        {
            LogHelper.Print("无法获取地图信息");
            return;
        }

        if (PartyHelper.Party == null || PartyHelper.Party.Count == 0)
        {
            LogHelper.Print("队伍信息为空");
            return;
        }

        // 为每个队伍成员添加羽毛危险区
        foreach (var member in PartyHelper.Party)
        {
            uint randomId = (uint)random.Next(1, int.MaxValue);
            var featherShape = new CircleShape 
            { 
                Origin = member.Position, 
                Radius = FEATHER_RADIUS 
            };
            var dangerShape = new DangerShape(0, FEATHER_DURATION, featherShape);
            map.AddDangerShape(randomId, dangerShape);
        }

        LogHelper.Print($"羽毛出现，为 {PartyHelper.Party.Count} 个队伍成员添加危险区");
    }

    public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
    {
        // 检查是否为羽毛动作时间线事件
        if (condParams is ActorControlCondParams actorControl &&
            actorControl.id == ActorControlCategory.PlayActionTimeline &&
            actorControl.arg0 == 7738)
        {
            // 获取当前时间戳（单位：毫秒）
            long currentTime = Environment.TickCount64;

            // 检查冷却时间是否已过
            if (!hasTriggeredThisRound || currentTime - lastTriggerTime > COOLDOWN_MS)
            {
                hasTriggeredThisRound = true;
                lastTriggerTime = currentTime;
                AddFeatherDangerZones();
            }
        }

        return false;
    }
}

using AEAssist;
using System.Numerics;
