namespace AutoRaidHelper.Triggers.UWU.风神
{
    public class 烈风刃 : ITriggerScript
    {
        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {
            var mapBaseName = "w1fz";
            if (scriptEnv.KV.TryGetValue("风神", out var p1))
            {
                if (!(bool)p1)
                {
                    return false;
                }
            }

            if (condParams is EnemyCastSpellCondParams enemyCastSpell)
            {
                if (enemyCastSpell.SpellId == 11080) // 烈风刃
                {
                    LogHelper.Print($"烈风刃目标坐标: {enemyCastSpell.Object.Position}");
                    // 使用解析移动系统处理移动
                    var targetX = enemyCastSpell.Object.Position.X;
                    var targetY = enemyCastSpell.Object.Position.Y;
                    var targetZ = enemyCastSpell.Object.Position.Z;
                    // 通过默语日志触发解析移动系统
                    Core.Resolve<MemApiChatMessage>().Echo($"移动到{targetX},{targetY},{targetZ}");

                    return false;
                }
                return false;
            }
            return false;
        }
    }
}

using AEAssist;
using System.Numerics;
using AEAssist.Avoid;
using AEAssist.CombatRoutine.Trigger;
using AEAssist.CombatRoutine.Trigger.TriggerAction;
using AEAssist.CombatRoutine.Trigger.Node;
using AEAssist.CombatRoutine.Trigger.TriggerCond;
using AEAssist.DynamicComplie;
using AEAssist.Helper;
using AEAssist.MemoryApi;
using System;
using System.Threading.Tasks;
using System.Collections.Generic;

namespace AutoRaidHelper.Triggers.UWU;

public class 解析移动 : ITriggerScript
{
    private List<(Vector3 center, float radius)> dangerZones = new List<(Vector3, float)>();
    private Vector3? currentTarget = null;
    private const float ARRIVAL_THRESHOLD = 0.1f; // 到达目标点的距离容差

    // 指令队列相关
    private readonly Queue<MoveCommand> moveQueue = new Queue<MoveCommand>();
    private DateTime? moveStartTime = null;
    private bool isExecutingMove = false;

    // 移动指令数据结构
    private class MoveCommand
    {
        public Vector3 TargetPoint { get; set; }
        public int? DelayMs { get; set; }
        public float? RandomOffset { get; set; }
        public string MatchedRole { get; set; } = "ALL"; // 默认为ALL
        public List<string> ExcludedRoles { get; set; } = new List<string>(); // 排除的角色列表
        public DateTime? StartTime { get; set; }
        public bool IsDelayed => DelayMs.HasValue && DelayMs.Value > 0;
        public bool IsReadyToExecute => !IsDelayed || (StartTime.HasValue && (DateTime.Now - StartTime.Value).TotalMilliseconds >= DelayMs.Value);
    }

    // PositionHelper 内部类
    public static class PositionHelper
    {
        // 静态 Random 实例以避免重复生成导致的随机性问题
        private static readonly Random rand = new Random();

        /// <summary>
        /// 返回一个在基准位置的 X 和 Z 轴上随机偏移的坐标，Y 轴保持不变
        /// </summary>
        /// <param name="basePosition">基准坐标</param>
        /// <param name="maxOffset">最大偏移量（X 和 Z 轴的偏移范围为 -maxOffset 到 +maxOffset）</param>
        /// <returns>带有随机偏移的新坐标（Y 轴不变）</returns>
        public static Vector3 GetRandomOffsetPosition(Vector3 basePosition, float maxOffset)
        {
            // 生成 X 和 Z 轴的随机偏移量
            float offsetX = (float)(rand.NextDouble() * 2 - 1) * maxOffset;
            float offsetZ = (float)(rand.NextDouble() * 2 - 1) * maxOffset;

            // 返回新的坐标，Y 轴保持不变
            return new Vector3(
                basePosition.X + offsetX,
                basePosition.Y,
                basePosition.Z + offsetZ
            );
        }

        /// <summary>
        /// 移动到目标位置并添加调试点，应用随机偏移
        /// </summary>
        /// <param name="basePosition">目标基准位置</param>
        /// <param name="maxOffset">最大偏移量</param>
        public static void MoveToPositionWithOffset(Vector3 basePosition, float maxOffset)
        {
            Share.TrustDebugPoint.Clear();
            // 获取带有随机偏移的目标位置
            Vector3 targetPosition = GetRandomOffsetPosition(basePosition, maxOffset);
            // 执行移动
            Core.Resolve<MemApiMove>().MoveToTarget(targetPosition);
            Share.TrustDebugPoint.Add(targetPosition);
        }
    }

    // DirectionHelper 内部类
    public static class DirectionHelper
    {
        // 定义四个方向参考点（根据实际地图坐标调整）
        private static readonly Vector3 NorthPoint = new Vector3(100, 0, 80);
        private static readonly Vector3 SouthPoint = new Vector3(100, 0, 120);
        private static readonly Vector3 EastPoint = new Vector3(80, 0, 100);
        private static readonly Vector3 WestPoint = new Vector3(120, 0, 100);
        private const float Threshold = 1f;//  阈值

        public enum Direction
        {
            None,
            North,
            South,
            East,
            West
        }

        public static Direction GetDirection(Vector3 position)
        {
            if (Vector3.Distance(position, NorthPoint) < Threshold)
                return Direction.North;
            if (Vector3.Distance(position, SouthPoint) < Threshold)
                return Direction.South;
            if (Vector3.Distance(position, EastPoint) < Threshold)
                return Direction.East;
            if (Vector3.Distance(position, WestPoint) < Threshold)
                return Direction.West;
            return Direction.None;
        }
    }

    private ScriptEnv currentScriptEnv = null;

    public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
    {
        currentScriptEnv = scriptEnv; // 保存scriptEnv引用

        if (condParams is GameLogCondParams logParams)
        {
            // 检查是否是停止移动指令
            if (logParams.Log == "停止移动" && logParams.LogType == 56)
            {
                LogHelper.Print("检测到停止移动指令，清除所有移动目标和队列");
                ClearTarget();
                return false;
            }

            // 检查状态查询指令
            if (logParams.LogType == 56)
            {
                switch (logParams.Log)
                {
                    case "获取当前目标点":
                        LogHelper.Print("检测到获取当前目标点指令");
                        GetCurrentTarget();
                        return false;

                    case "获取移动状态":
                        LogHelper.Print("检测到获取移动状态指令");
                        IsMoving();
                        return false;

                    case "获取队列状态":
                        LogHelper.Print("检测到获取队列状态指令");
                        IsQueueEmpty();
                        return false;

                    case "获取队列长度":
                        LogHelper.Print("检测到获取队列长度指令");
                        GetQueueLength();
                        return false;

                    case "获取延迟状态":
                        LogHelper.Print("检测到获取延迟状态指令");
                        IsDelaying();
                        return false;

                    case "获取完整状态":
                        LogHelper.Print("检测到获取完整状态指令");
                        GetStatus();
                        return false;

                    case "获取当前位置方向":
                        LogHelper.Print("检测到获取当前位置方向指令");
                        GetCurrentDirection();
                        return false;
                }
            }

            // 检查是否是移动指令日志
            if (logParams.Log.StartsWith("移动到") && logParams.LogType == 56)
            {
                try
                {
                    // 解析坐标和参数
                    var moveData = ParseMoveCommand(logParams.Log);
                    if (moveData.HasValue)
                    {
                        var (targetPoint, delay, offset, role, excludedRoles) = moveData.Value;

                        // 创建新的移动指令并加入队列
                        var moveCommand = new MoveCommand
                        {
                            TargetPoint = targetPoint,
                            DelayMs = delay,
                            RandomOffset = offset,
                            MatchedRole = role,
                            ExcludedRoles = excludedRoles,
                            StartTime = delay.HasValue && delay.Value > 0 ? DateTime.Now : null
                        };

                        // 检查角色匹配
                        var currentRole = GetCurrentRole();

                        // 检查是否在排除列表中
                        if (moveCommand.ExcludedRoles.Contains(currentRole))
                        {
                            LogHelper.Print($"当前角色 {currentRole} 在排除列表中，忽略指令。排除角色: {string.Join(", ", moveCommand.ExcludedRoles)}");
                            return false;
                        }

                        // 检查角色匹配
                        if (moveCommand.MatchedRole != "ALL" && moveCommand.MatchedRole != currentRole)
                        {
                            LogHelper.Print($"角色不匹配，忽略指令。当前角色: {currentRole}，指令角色: {moveCommand.MatchedRole}");
                            return false;
                        }

                        moveQueue.Enqueue(moveCommand);

                        LogHelper.Print($"添加移动指令到队列: {logParams.Log} -> 目标点: ({targetPoint.X:F2}, {targetPoint.Y:F2}, {targetPoint.Z:F2})");
                        if (delay.HasValue)
                        {
                            LogHelper.Print($"延迟移动: {delay}ms");
                        }
                        if (offset.HasValue)
                        {
                            LogHelper.Print($"随机偏移: ±{offset:F2}");
                        }
                        if (moveCommand.MatchedRole != "ALL")
                        {
                            LogHelper.Print($"角色限制: {moveCommand.MatchedRole}");
                        }
                        if (moveCommand.ExcludedRoles.Count > 0)
                        {
                            LogHelper.Print($"排除角色: {string.Join(", ", moveCommand.ExcludedRoles)}");
                        }
                        LogHelper.Print($"当前队列长度: {moveQueue.Count}");
                    }
                    else
                    {
                        LogHelper.PrintError($"无法解析移动坐标: {logParams.Log}");
                    }
                }
                catch (Exception ex)
                {
                    LogHelper.PrintError($"解析移动指令时出错: {ex.Message}");
                }
            }
        }

        // 处理队列中的移动指令
        ProcessMoveQueue();

        // 检查是否到达当前目标点
        if (currentTarget.HasValue && !isExecutingMove)
        {
            var distance = Vector3.Distance(Core.Me.Position, currentTarget.Value);
            if (distance < ARRIVAL_THRESHOLD)
            {
                Core.Resolve<MemApiMove>().CancelMove();
                LogHelper.Print($"到达目标点: ({currentTarget.Value.X:F2}, {currentTarget.Value.Y:F2}, {currentTarget.Value.Z:F2})");
                currentTarget = null;

                // 到达目标点后，继续处理队列中的下一个指令
                ProcessMoveQueue();
            }
        }

        return false; // 始终返回false以保持检测
    }

    /// <summary>
    /// 处理移动队列
    /// </summary>
    private void ProcessMoveQueue()
    {
        // 如果当前没有在执行移动，且队列不为空
        if (!isExecutingMove && moveQueue.Count > 0)
        {
            var nextCommand = moveQueue.Peek();

            // 检查指令是否准备好执行
            if (nextCommand.IsReadyToExecute)
            {
                // 从队列中取出指令
                moveQueue.Dequeue();

                // 执行移动
                ExecuteMove(nextCommand.TargetPoint, nextCommand.RandomOffset);
                currentTarget = nextCommand.TargetPoint;
                isExecutingMove = true;

                LogHelper.Print($"执行队列中的移动指令: ({nextCommand.TargetPoint.X:F2}, {nextCommand.TargetPoint.Y:F2}, {nextCommand.TargetPoint.Z:F2})");
                LogHelper.Print($"剩余队列长度: {moveQueue.Count}");
            }
            else if (nextCommand.IsDelayed && !nextCommand.StartTime.HasValue)
            {
                // 开始延迟计时
                nextCommand.StartTime = DateTime.Now;
                LogHelper.Print($"开始延迟移动计时: {nextCommand.DelayMs}ms");
            }
        }

        // 如果当前正在移动，检查是否到达目标点
        if (isExecutingMove && currentTarget.HasValue)
        {
            var distance = Vector3.Distance(Core.Me.Position, currentTarget.Value);
            if (distance < ARRIVAL_THRESHOLD)
            {
                Core.Resolve<MemApiMove>().CancelMove();
                LogHelper.Print($"到达目标点: ({currentTarget.Value.X:F2}, {currentTarget.Value.Y:F2}, {currentTarget.Value.Z:F2})");
                currentTarget = null;
                isExecutingMove = false;

                // 继续处理队列中的下一个指令
                ProcessMoveQueue();
            }
        }
    }

    /// <summary>
    /// 执行移动操作，使用PositionHelper处理随机偏移，并检测安全点
    /// </summary>
    private void ExecuteMove(Vector3 basePosition, float? offset)
    {
        Vector3 targetPosition;

        if (offset.HasValue && offset.Value > 0)
        {
            // 使用PositionHelper进行随机偏移
            targetPosition = PositionHelper.GetRandomOffsetPosition(basePosition, offset.Value);
            LogHelper.Print($"应用随机偏移: 基准点({basePosition.X:F2}, {basePosition.Y:F2}, {basePosition.Z:F2}) -> 目标点({targetPosition.X:F2}, {targetPosition.Y:F2}, {targetPosition.Z:F2})");
        }
        else
        {
            targetPosition = basePosition;
        }

        // 检测目标点是否为安全点
        var finalTarget = CheckAndFindSafePoint(targetPosition);

        Core.Resolve<MemApiMove>().MoveToTarget(finalTarget);
    }

    /// <summary>
    /// 检测目标点是否为安全点，如果不是则寻找最近的安全点
    /// </summary>
    /// <param name="targetPosition">目标位置</param>
    /// <returns>安全的目标位置</returns>
    private Vector3 CheckAndFindSafePoint(Vector3 targetPosition)
    {
        try
        {
            // 固定地图名称为w1fz
            var mapBaseName = "w1fz";

            LogHelper.Print($"尝试获取地图信息: {mapBaseName}");

            if (AvoidManager.Instance.GetMap(mapBaseName, out var map))
            {
                // 检查目标点是否为安全点
                if (map.IsPosSafe(targetPosition))
                {
                    LogHelper.Print($"目标点({targetPosition.X:F2}, {targetPosition.Y:F2}, {targetPosition.Z:F2})是安全点");
                    return targetPosition;
                }
                else
                {
                    LogHelper.Print($"目标点({targetPosition.X:F2}, {targetPosition.Y:F2}, {targetPosition.Z:F2})不是安全点，寻找最近安全点");

                    // 寻找最近的安全点
                    if (map.FindNearestSafePoint(targetPosition, 30, out var safePoint))
                    {
                        LogHelper.Print($"找到最近安全点: ({safePoint.X:F2}, {safePoint.Y:F2}, {safePoint.Z:F2})");
                        return safePoint;
                    }
                    else
                    {
                        LogHelper.Print($"无法找到安全点，使用原始目标点: ({targetPosition.X:F2}, {targetPosition.Y:F2}, {targetPosition.Z:F2})");
                        return targetPosition;
                    }
                }
            }
            else
            {
                LogHelper.Print($"无法获取地图信息，地图名称: {mapBaseName}，使用原始目标点: ({targetPosition.X:F2}, {targetPosition.Y:F2}, {targetPosition.Z:F2})");
                return targetPosition;
            }
        }
        catch (Exception ex)
        {
            LogHelper.PrintError($"安全点检测时发生异常: {ex.Message}");
            LogHelper.PrintError($"异常堆栈: {ex.StackTrace}");
            return targetPosition;
        }
    }

    /// <summary>
    /// 解析移动命令，支持延迟、随机偏移、角色参数和排除角色参数
    /// 格式: "移动到x,y,z[延迟ms][偏移量][角色][排除角色]"
    /// 示例: "移动到100,200,300" - 基本移动
    /// 示例: "移动到100,200,300:500" - 延迟500ms移动
    /// 示例: "移动到100,200,300:500:2.5" - 延迟500ms移动，随机偏移±2.5
    /// 示例: "移动到100,200,300::2.5" - 立即移动，随机偏移±2.5
    /// 示例: "移动到100,200,300:500:2.5:D3" - 延迟500ms移动，随机偏移±2.5，仅D3角色执行
    /// 示例: "移动到100,200,300:500:2.5:ALL" - 延迟500ms移动，随机偏移±2.5，所有角色执行
    /// 示例: "移动到100,200,300:500:2.5:ALL:MT,ST" - 延迟500ms移动，随机偏移±2.5，所有角色执行，但排除MT和ST
    /// 示例: "移动到100,200,300:500:2.5::MT,ST" - 延迟500ms移动，随机偏移±2.5，所有角色执行，但排除MT和ST
    /// </summary>
    private (Vector3 position, int? delay, float? offset, string role, List<string> excludedRoles)? ParseMoveCommand(string logMessage)
    {
        try
        {
            // 移除"移动到"前缀
            var commandPart = logMessage.Replace("移动到", "").Trim();

            // 分割命令部分（坐标:延迟:偏移:角色:排除角色）
            var parts = commandPart.Split(':');

            // 解析坐标部分
            var coordinatePart = parts[0];
            var coordinates = ParseCoordinates(coordinatePart);
            if (!coordinates.HasValue)
            {
                return null;
            }

            int? delay = null;
            float? offset = null;
            string role = "ALL"; // 默认为ALL
            List<string> excludedRoles = new List<string>();

            // 解析延迟参数
            if (parts.Length > 1 && !string.IsNullOrEmpty(parts[1]))
            {
                if (int.TryParse(parts[1].Trim(), out int delayValue))
                {
                    delay = delayValue;
                }
                else
                {
                    LogHelper.PrintError($"无法解析延迟参数: {parts[1]}");
                    return null;
                }
            }

            // 解析偏移参数
            if (parts.Length > 2 && !string.IsNullOrEmpty(parts[2]))
            {
                if (float.TryParse(parts[2].Trim(), out float offsetValue))
                {
                    offset = offsetValue;
                }
                else
                {
                    LogHelper.PrintError($"无法解析偏移参数: {parts[2]}");
                    return null;
                }
            }

            // 解析角色参数
            if (parts.Length > 3 && !string.IsNullOrEmpty(parts[3]))
            {
                role = parts[3].Trim().ToUpper();
                // 验证角色参数
                var validRoles = new[] { "D1", "D2", "D3", "D4", "H1", "H2", "MT", "ST", "ALL" };
                if (!validRoles.Contains(role))
                {
                    LogHelper.PrintError($"无效的角色参数: {role}，有效角色: {string.Join(", ", validRoles)}");
                    return null;
                }
            }

            // 解析排除角色参数
            if (parts.Length > 4 && !string.IsNullOrEmpty(parts[4]))
            {
                var excludedRolesStr = parts[4].Trim();
                var excludedRoleArray = excludedRolesStr.Split(',');

                foreach (var excludedRole in excludedRoleArray)
                {
                    var trimmedRole = excludedRole.Trim().ToUpper();
                    // 验证排除角色参数
                    var validRoles = new[] { "D1", "D2", "D3", "D4", "H1", "H2", "MT", "ST" };
                    if (validRoles.Contains(trimmedRole))
                    {
                        excludedRoles.Add(trimmedRole);
                    }
                    else
                    {
                        LogHelper.PrintError($"无效的排除角色参数: {trimmedRole}，有效角色: {string.Join(", ", validRoles)}");
                        return null;
                    }
                }
            }

            return (coordinates.Value, delay, offset, role, excludedRoles);
        }
        catch (Exception ex)
        {
            LogHelper.PrintError($"解析移动命令时发生异常: {ex.Message}");
            return null;
        }
    }

    /// <summary>
    /// 解析"x,y,z"格式的坐标
    /// </summary>
    /// <param name="coordinatePart">坐标字符串</param>
    /// <returns>解析后的Vector3坐标，解析失败返回null</returns>
    private Vector3? ParseCoordinates(string coordinatePart)
    {
        try
        {
            // 分割坐标
            var parts = coordinatePart.Split(',');
            if (parts.Length != 3)
            {
                LogHelper.PrintError($"坐标格式错误，需要3个值，实际得到{parts.Length}个: {coordinatePart}");
                return null;
            }

            // 解析X, Z, Y坐标（按照Vector3(X,z,y)的格式）
            if (!float.TryParse(parts[0].Trim(), out float x))
            {
                LogHelper.PrintError($"无法解析X坐标: {parts[0]}");
                return null;
            }

            if (!float.TryParse(parts[1].Trim(), out float z))
            {
                LogHelper.PrintError($"无法解析Z坐标: {parts[1]}");
                return null;
            }

            if (!float.TryParse(parts[2].Trim(), out float y))
            {
                LogHelper.PrintError($"无法解析Y坐标: {parts[2]}");
                return null;
            }

            // 返回Vector3(X,z,y)格式的坐标
            return new Vector3(x, z, y);
        }
        catch (Exception ex)
        {
            LogHelper.PrintError($"解析坐标时发生异常: {ex.Message}");
            return null;
        }
    }

    /// <summary>
    /// 获取当前目标点
    /// </summary>
    public void GetCurrentTarget()
    {
        if (currentTarget.HasValue)
        {
            LogHelper.Print($"当前目标点: ({currentTarget.Value.X:F2}, {currentTarget.Value.Y:F2}, {currentTarget.Value.Z:F2})");
        }
        else
        {
            LogHelper.Print("当前无目标点");
        }
    }

    /// <summary>
    /// 清除当前目标和队列
    /// </summary>
    public void ClearTarget()
    {
        currentTarget = null;
        isExecutingMove = false;
        moveQueue.Clear();
        Core.Resolve<MemApiMove>().CancelMove();
        LogHelper.Print("已清除所有移动目标和队列");
    }

    /// <summary>
    /// 检查是否正在移动
    /// </summary>
    public void IsMoving()
    {
        bool moving = currentTarget.HasValue || isExecutingMove;
        LogHelper.Print($"移动状态: {(moving ? "正在移动" : "未移动")}");
    }

    /// <summary>
    /// 检查队列是否为空
    /// </summary>
    public void IsQueueEmpty()
    {
        bool empty = moveQueue.Count == 0;
        LogHelper.Print($"队列状态: {(empty ? "队列为空" : "队列不为空")}");
    }

    /// <summary>
    /// 获取队列长度
    /// </summary>
    public void GetQueueLength()
    {
        LogHelper.Print($"当前队列长度: {moveQueue.Count}");
    }

    /// <summary>
    /// 检查是否正在延迟等待
    /// </summary>
    public void IsDelaying()
    {
        bool delaying = moveQueue.Count > 0 && moveQueue.Peek().IsDelayed && !moveQueue.Peek().IsReadyToExecute;
        LogHelper.Print($"延迟状态: {(delaying ? "正在延迟等待" : "无延迟等待")}");
    }

    /// <summary>
    /// 获取完整状态信息
    /// </summary>
    public void GetStatus()
    {
        LogHelper.Print("=== 移动系统状态 ===");

        // 当前目标
        if (currentTarget.HasValue)
        {
            LogHelper.Print($"当前目标点: ({currentTarget.Value.X:F2}, {currentTarget.Value.Y:F2}, {currentTarget.Value.Z:F2})");
        }
        else
        {
            LogHelper.Print("当前目标点: 无");
        }

        // 移动状态
        bool moving = currentTarget.HasValue || isExecutingMove;
        LogHelper.Print($"移动状态: {(moving ? "正在移动" : "未移动")}");

        // 队列状态
        LogHelper.Print($"队列长度: {moveQueue.Count}");

        // 延迟状态
        bool delaying = moveQueue.Count > 0 && moveQueue.Peek().IsDelayed && !moveQueue.Peek().IsReadyToExecute;
        LogHelper.Print($"延迟状态: {(delaying ? "正在延迟等待" : "无延迟等待")}");

        // 队列详情
        if (moveQueue.Count > 0)
        {
            LogHelper.Print("队列详情:");
            int index = 1;
            foreach (var command in moveQueue)
            {
                string delayInfo = command.DelayMs.HasValue ? $"延迟{command.DelayMs}ms" : "立即执行";
                string offsetInfo = command.RandomOffset.HasValue ? $"偏移±{command.RandomOffset:F2}" : "无偏移";
                string roleInfo = command.MatchedRole != "ALL" ? $"角色:{command.MatchedRole}" : "角色:ALL";
                string excludedInfo = command.ExcludedRoles.Count > 0 ? $"排除:{string.Join(",", command.ExcludedRoles)}" : "无排除";
                LogHelper.Print($"  {index}. 目标点({command.TargetPoint.X:F2}, {command.TargetPoint.Y:F2}, {command.TargetPoint.Z:F2}) - {delayInfo} - {offsetInfo} - {roleInfo} - {excludedInfo}");
                index++;
            }
        }

        LogHelper.Print("==================");
    }

    /// <summary>
    /// 获取当前角色
    /// </summary>
    /// <returns>当前角色名称</returns>
    private string GetCurrentRole()
    {
        try
        {
            // 从ScriptEnv中获取当前角色
            if (currentScriptEnv?.KV != null && currentScriptEnv.KV.ContainsKey("DetectedRole"))
            {
                return currentScriptEnv.KV["DetectedRole"]?.ToString() ?? "UNKNOWN";
            }

            // 如果无法获取，返回UNKNOWN
            return "UNKNOWN";
        }
        catch (Exception ex)
        {
            LogHelper.PrintError($"获取当前角色时发生异常: {ex.Message}");
            return "UNKNOWN";
        }
    }

    /// <summary>
    /// 获取当前位置方向
    /// </summary>
    public void GetCurrentDirection()
    {
        var currentPosition = Core.Me.Position;
        var direction = DirectionHelper.GetDirection(currentPosition);

        string directionName = direction switch
        {
            DirectionHelper.Direction.North => "北",
            DirectionHelper.Direction.South => "南",
            DirectionHelper.Direction.East => "东",
            DirectionHelper.Direction.West => "西",
            DirectionHelper.Direction.None => "未知",
            _ => "未知"
        };

        LogHelper.Print($"当前位置: ({currentPosition.X:F2}, {currentPosition.Y:F2}, {currentPosition.Z:F2})");
        LogHelper.Print($"当前位置方向: {directionName}");
    }
}

using AEAssist;
using System.Numerics;
using AEAssist.CombatRoutine.Trigger;
using AEAssist.CombatRoutine.Trigger.Node;
using AEAssist.Helper;
using AEAssist.Extension;
using AEAssist.MemoryApi;
using AEAssist.CombatRoutine.Trigger.TriggerCond;
using AEAssist.CombatRoutine.Module;
using System;
using System.Threading.Tasks;

namespace AutoRaidHelper.Triggers.UWU.风神;

public class std3接线 : ITriggerScript
{
    private bool isMovingBetweenPoints = false; // 用于标记是否在两个点之间移动
    private Vector3 pointA = new Vector3(90f, 0, 103f); // 第一个固定坐标
    private Vector3 pointB = new Vector3(90f, 0, 97f); // 第二个固定坐标
    private Vector3 pointC = new Vector3(110f, 0, 103f);
    private Vector3 pointD = new Vector3(110f, 0, 97f);

    public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
    {
        if (scriptEnv.KV.TryGetValue("风神", out var p1) && (bool)p1)
        {
            if (condParams is TetherCondParams tether && tether.Args0 == 4)
            {
                LogHelper.Print("风神线");

                // 从 scriptEnv.KV 获取 DetectedRole
                var detectedRole = scriptEnv.KV["DetectedRole"] as string;
                if (string.IsNullOrEmpty(detectedRole))
                {
                    LogHelper.Print("未检测到角色职能");
                    return false;
                }

                Vector3 targetPoint;
                if (detectedRole.Equals("ST", StringComparison.OrdinalIgnoreCase))
                {
                    targetPoint = pointC; // ST 去 C/D 点
                }
                else if (detectedRole.Equals("D3", StringComparison.OrdinalIgnoreCase))
                {
                    targetPoint = pointA; // D3 去 A/B 点
                }
                else
                {
                    LogHelper.Print($"非接线职能: {detectedRole}");
                    return true; // 不支持的角色直接返回 true
                }

                // 使用解析移动系统处理移动
                var targetX = targetPoint.X;
                var targetY = targetPoint.Y;
                var targetZ = targetPoint.Z;

                // 通过默语日志触发解析移动系统
                Core.Resolve<MemApiChatMessage>().Echo($"移动到{targetX},{targetY},{targetZ}");

                if (tether.Right != Core.Me)
                {
                    if (!isMovingBetweenPoints)
                    {
                        LogHelper.Print("没接到线，开始上下动");
                        StartMovingBetweenPoints(detectedRole); // 根据职能开始移动
                        isMovingBetweenPoints = true; // 设置标志表示已经在运动
                        return false;
                    }
                }
                else
                {
                    return true;
                }
            }
        }

        return false;
    }

    public void StartMovingBetweenPoints(string role)
    {
        Task.Run(async () =>
        {
            Vector3 target1 = role == "ST" ? pointC : pointA;
            Vector3 target2 = role == "ST" ? pointD : pointB;

            Vector3 target = target1; // 初始目标为第一个坐标点
            bool reachedTarget = false;
            DateTime startTime = DateTime.Now;
            const int timeoutMs = 8000; // 8秒超时

            while (!reachedTarget && (DateTime.Now - startTime).TotalMilliseconds < timeoutMs)
            {
                // 使用解析移动系统处理移动
                var targetX = target.X;
                var targetY = target.Y;
                var targetZ = target.Z;

                // 通过默语日志触发解析移动系统
                Core.Resolve<MemApiChatMessage>().Echo($"移动到{targetX},{targetY},{targetZ}");

                if (Vector3.Distance(Core.Me.Position, target) < 0.1f)
                {
                    LogHelper.Print("到达目标点，前往下一个目标点");
                    target = target == target1 ? target2 : target1;
                }

                if (Core.Resolve<MemApiTether>().GetUnitTetherId(Core.Me) == 4)
                {
                    LogHelper.Print("检测到 Tether ID 为 4，停止移动");
                    reachedTarget = true;
                }

                await Task.Delay(50);
            }

            Core.Resolve<MemApiMove>().CancelMove();
            isMovingBetweenPoints = false;
            LogHelper.Print("移动逻辑结束（超时或任务完成）");
        });
    }
}

using AEAssist;
using System.Numerics;
using AEAssist.CombatRoutine.Trigger;
using AEAssist.CombatRoutine.Trigger.Node;
using AEAssist.Helper;
using AEAssist.Extension;
using AEAssist.MemoryApi;
using AEAssist.CombatRoutine.Trigger.TriggerCond;
using AEAssist.CombatRoutine.Module;
using AEAssist.Avoid;
using ECommons.GameHelpers;
using System.Linq;

namespace AutoRaidHelper.Triggers.UWU.风神
{
    public class 风压 : ITriggerScript
    {
        // 风压处理相关变量
        private Vector3 风圈中心pos;
        private bool 等待Buff处理 = false;
        private DateTime 初始危险区时间;
        private DateTime lastD1D2Check = DateTime.MinValue;
        private bool 正在执行风压消除 = false;
        private Vector3 目标ST位置;
        private const int 初始危险区持续时间 = 4000; // 4秒
        private const int H1H2D3D4危险区持续时间 = 18000; // 18秒
        private const int D1D2危险区持续时间 = 8000; // 8秒
        private const int D1D2检查间隔 = 500; // D1/D2检查间隔500毫秒
        private static readonly Random random = new Random();

        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {
            // 检查风神阶段
            if (scriptEnv.KV.TryGetValue("风神", out var p1))
            {
                if (!(bool)p1)
                {
                    return false;
                }
            }

            // 1. 高气压圈读条检测
            if (condParams is EnemyCastSpellCondParams enemyCastSpell)
            {
                if (enemyCastSpell.SpellId == 11078) // 刺羽死亡大暴风
                {
                    风圈中心pos = enemyCastSpell.CastPos;
                    添加风圈危险区(初始危险区持续时间);
                    初始危险区时间 = DateTime.Now;
                    等待Buff处理 = true;
                    LogHelper.Print($"高气压圈读条检测，风圈位置：{风圈中心pos}");
                    return false;
                }
            }

            // 2. 低气压Buff添加检测
            else if (condParams is AddStatusCondParams addStatus)
            {
                if (addStatus.StatusId == 1525 && addStatus.Target == Core.Me) // 低气压
                {
                    var detectedRole = scriptEnv.KV["DetectedRole"] as string;

                    if (!string.IsNullOrEmpty(detectedRole))
                    {
                        // H1/H2/D3/D4职能处理
                        if (new[] { "H1", "H2", "D3", "D4" }.Any(role => 
                            role.Equals(detectedRole, StringComparison.OrdinalIgnoreCase)))
                        {
                            LogHelper.Print($"H1/H2/D3/D4职能处理：{detectedRole}");
                            H1H2D3D4职能处理();
                        }
                        // D1/D2职能处理 - 只记录角色，持续检查在下面
                        else if (new[] { "D1", "D2" }.Any(role => 
                            role.Equals(detectedRole, StringComparison.OrdinalIgnoreCase)))
                        {
                            LogHelper.Print($"D1/D2职能处理开始：{detectedRole}");
                            lastD1D2Check = DateTime.Now;
                        }
                    }
                    return false;
                }
            }

            // 3. 低气压Buff移除检测
            else if (condParams is RemoveStatusCondParams removeStatus)
            {
                if (removeStatus.StatusId == 1525 && removeStatus.Target == Core.Me)
                {
                    // 停止移动
                    Core.Resolve<MemApiChatMessage>().Echo("停止移动");

                    var detectedRole = scriptEnv.KV["DetectedRole"] as string;

                    if (!string.IsNullOrEmpty(detectedRole))
                    {
                        // H1/H2/D3/D4职能解除后处理
                        if (new[] { "H1", "H2", "D3", "D4" }.Any(role => 
                            role.Equals(detectedRole, StringComparison.OrdinalIgnoreCase)))
                        {
                            LogHelper.Print($"H1/H2/D3/D4职能解除后处理：{detectedRole}");
                            添加风圈危险区(H1H2D3D4危险区持续时间);
                        }
                        // D1/D2职能解除后处理
                        else if (new[] { "D1", "D2" }.Any(role => 
                            role.Equals(detectedRole, StringComparison.OrdinalIgnoreCase)))
                        {
                            LogHelper.Print($"D1/D2职能解除后处理：{detectedRole}");
                            添加风圈危险区(D1D2危险区持续时间);

                            // 如果自身不是ST/MT职能，则执行风压消除处理
                            if (!new[] { "ST", "MT" }.Any(role => 
                                role.Equals(detectedRole, StringComparison.OrdinalIgnoreCase)))
                            {
                                开始风压消除处理();
                            }
                        }
                    }
                    return false;
                }
            }

            // 持续D1/D2职能处理检查
            if (等待Buff处理 && (DateTime.Now - lastD1D2Check).TotalMilliseconds >= D1D2检查间隔)
            {
                var detectedRole = scriptEnv.KV["DetectedRole"] as string;
                if (!string.IsNullOrEmpty(detectedRole) && 
                    new[] { "D1", "D2" }.Any(role => role.Equals(detectedRole, StringComparison.OrdinalIgnoreCase)))
                {
                    D1D2职能处理();
                    lastD1D2Check = DateTime.Now;
                }
            }

            // 持续风压消除处理检查
            if (正在执行风压消除)
            {
                return 执行风压消除处理();
            }

            return false;
        }

        /// <summary>
        /// 添加风圈危险区
        /// </summary>
        private void 添加风圈危险区(int duration)
        {
            var mapBaseName = Core.Resolve<MemApiZoneInfo>().GetCurrZoneInfo().MapBaseName;
            uint randomId = (uint)random.Next(1, int.MaxValue);

            if (AvoidManager.Instance.GetMap(mapBaseName, out var map))
            {
                map.AddDangerShape(randomId, new DangerShape(0, duration, new CircleShape()
                {
                    Origin = 风圈中心pos,
                    Radius = 7f,
                }));
                LogHelper.Print($"添加风圈危险区，持续时间：{duration}ms");
            }
        }

        /// <summary>
        /// H1/H2/D3/D4职能处理
        /// </summary>
        private void H1H2D3D4职能处理()
        {
            // 使用解析移动系统处理移动
            var targetX = 风圈中心pos.X;
            var targetY = 风圈中心pos.Y;
            var targetZ = 风圈中心pos.Z;

            // 通过默语日志触发解析移动系统
            Core.Resolve<MemApiChatMessage>().Echo($"移动到{targetX},{targetZ},{targetY}");

            LogHelper.Print("H1/H2/D3/D4职能处理：移动到风圈中心，等待buff消除");
        }

        /// <summary>
        /// D1/D2职能处理
        /// </summary>
        private void D1D2职能处理()
        {
            // 检查是否有血量低于8000的队友
            bool 有低血量队友 = PartyHelper.Party.Any(x => x.CurrentHp <= 8000);

            if (!有低血量队友)
            {
                // 无低血量队友时才移动到风圈中心
                var targetX = 风圈中心pos.X;
                var targetY = 风圈中心pos.Y;
                var targetZ = 风圈中心pos.Z;

                // 通过默语日志触发解析移动系统
                Core.Resolve<MemApiChatMessage>().Echo($"移动到{targetX},{targetZ},{targetY}");

                LogHelper.Print("D1/D2职能处理：无低血量队友，移动到风圈中心，等待buff消除");
            }
            else
            {
                // 如果有低血量队友，停止移动
                Core.Resolve<MemApiChatMessage>().Echo("停止移动");
                LogHelper.Print("检测到低血量队友，D1/D2不移动");
            }
        }

        /// <summary>
        /// 开始风压消除处理
        /// </summary>
        private void 开始风压消除处理()
        {
            if (PartyHelper.Party != null && PartyHelper.Party.Count >= 3)
            {
                目标ST位置 = PartyHelper.Party[2].Position;
                正在执行风压消除 = true;

                // 使用解析移动系统处理移动
                var targetX = 目标ST位置.X;
                var targetY = 目标ST位置.Y;
                var targetZ = 目标ST位置.Z;

                // 通过默语日志触发解析移动系统
                Core.Resolve<MemApiChatMessage>().Echo($"移动到{targetX},{targetZ},{targetY}");

                LogHelper.Print("开始风压消除处理，前往ST位置");
            }
        }

        /// <summary>
        /// 执行风压消除处理
        /// </summary>
        private bool 执行风压消除处理()
        {
            // 距离检测和停止
            if (Vector3.Distance(Core.Me.Position, 目标ST位置) < 0.1f)
            {
                LogHelper.Print("到达ST位置，风压消除处理完成");
                Core.Resolve<MemApiChatMessage>().Echo("停止移动");
                正在执行风压消除 = false;
                return true; // 返回true表示处理完成
            }

            return false; // 继续处理
        }
    }
}

using AEAssist;
using System.Numerics;
using AEAssist.CombatRoutine.Trigger;
using AEAssist.CombatRoutine.Trigger.Node;
using AEAssist.Helper;
using AEAssist.Extension;
using AEAssist.MemoryApi;
using AEAssist.CombatRoutine.Trigger.TriggerCond;
using AEAssist.Avoid;

namespace AutoRaidHelper.Triggers.UWU;

public class 初始化 : ITriggerScript
{
    // 使用事件(True)，则脚本逻辑由Check方法控制。Check返回True代表此节点执行完毕


    public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
    {
        if (condParams is GameLogCondParams job)
        {
            string matchedRole = null;
            if (job.LogType == 56)
            {
                if (job.Log.Equals("D1", StringComparison.OrdinalIgnoreCase))
                    matchedRole = "D1";
                else if (job.Log.Equals("D2", StringComparison.OrdinalIgnoreCase))
                    matchedRole = "D2";
                else if (job.Log.Equals("D3", StringComparison.OrdinalIgnoreCase))
                    matchedRole = "D3";
                else if (job.Log.Equals("D4", StringComparison.OrdinalIgnoreCase))
                    matchedRole = "D4";
                else if (job.Log.Equals("H1", StringComparison.OrdinalIgnoreCase))
                    matchedRole = "H1";
                else if (job.Log.Equals("H2", StringComparison.OrdinalIgnoreCase))
                    matchedRole = "H2";
                else if (job.Log.Equals("MT", StringComparison.OrdinalIgnoreCase))
                    matchedRole = "MT";
                else if (job.Log.Equals("ST", StringComparison.OrdinalIgnoreCase))
                    matchedRole = "ST";
            }
            if (matchedRole != null)
            {
                // 此处可以对 matchedRole 做进一步处理
                LogHelper.Print($"检测到角色职能: {matchedRole}");
                scriptEnv.KV["DetectedRole"] = matchedRole;
                添加电网();
                if (matchedRole == "H1" || matchedRole == "H2" || matchedRole == "D3" || matchedRole == "D4")
                {
                    Vector3 targetPosition = new Vector3(100, 0, 100);
                    Core.Resolve<MemApiMove>().MoveToTarget(targetPosition);
                    LogHelper.Print($"{matchedRole} 正在前往目标位置：{targetPosition}");

                }
                return true;
            }
        }
        return false;
    }

    public void 添加电网()
    {
        var mapBaseName = "w1fz";
        if (AvoidManager.Instance.LoadMap(mapBaseName, 0.2f, out var map))
        {
            map.AgentRadius = 1f;
            map.AddDangerShape(1, new DangerShape(0, 0, new DonutShape() { SafeRadius = 20, Origin = new Vector3(100, 0, 100) }));
            LogHelper.Print("初始化绝神兵地图成功");
        }
    }
}

using AEAssist;
using System.Numerics;
using AEAssist.CombatRoutine.Trigger;
using AEAssist.CombatRoutine.Trigger.Node;
using AEAssist.Helper;
using AEAssist.Extension;
using AEAssist.MemoryApi;
using AEAssist.CombatRoutine.Trigger.TriggerCond;
using System;
using System.Collections.Generic;

namespace AutoRaidHelper.Triggers.UWU
{
    public class 检测阶段 : ITriggerScript
    {
        bool 火神 = false;
        bool 风神 = false;
        bool 土神 = false;
        bool 本体 = false;
        bool 转场lb = false;

        // 本体阶段细分
        bool 一运 = false;
        bool 二运 = false;
        bool 三运 = false;
        bool 家长会 = false;

        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {
            // 检查是否有手动阶段设置命令
            if (condParams is GameLogCondParams logParams)
            {
                // 检查是否是阶段设置指令
                if (logParams.LogType == 56)
                {
                    if (ProcessStageCommand(logParams.Log, scriptEnv))
                    {
                        return false; // 处理了命令，直接返回
                    }

                    // 处理状态查询命令
                    if (ProcessStatusQuery(logParams.Log))
                    {
                        return false; // 处理了查询，直接返回
                    }
                }
            }

            // 检测本体阶段技能（如果在本体阶段）
            if (本体 && condParams is EnemyCastSpellCondParams enemyCastSpell)
            {
                ProcessMainPhaseSkills(enemyCastSpell.SpellId, scriptEnv);
            }

            return false;
        }

        /// <summary>
        /// 处理阶段设置命令
        /// 支持的命令格式：
        /// - "设置阶段火神" - 设置为火神阶段
        /// - "设置阶段风神" - 设置为风神阶段
        /// - "设置阶段土神" - 设置为土神阶段
        /// - "设置阶段本体" - 设置为本体阶段
        /// - "设置阶段转场LB" - 设置为转场LB阶段
        /// - "设置阶段一运" - 设置本体阶段的一运
        /// - "设置阶段二运" - 设置本体阶段的二运
        /// - "设置阶段三运" - 设置本体阶段的三运
        /// - "设置阶段家长会" - 设置本体阶段的家长会
        /// - "清除阶段" - 清除所有阶段状态
        /// </summary>
        private bool ProcessStageCommand(string logMessage, ScriptEnv scriptEnv)
        {
            try
            {
                if (logMessage.StartsWith("设置阶段"))
                {
                    var stageName = logMessage.Replace("设置阶段", "").Trim();

                    switch (stageName)
                    {
                        case "火神":
                            SetStage(ref 火神, ref 风神, ref 土神, ref 本体, ref 转场lb, 
                                   true, false, false, false, false, "火神阶段（手动设置）", scriptEnv);
                            return true;

                        case "风神":
                            SetStage(ref 火神, ref 风神, ref 土神, ref 本体, ref 转场lb, 
                                   false, true, false, false, false, "风神阶段（手动设置）", scriptEnv);
                            return true;

                        case "土神":
                            SetStage(ref 火神, ref 风神, ref 土神, ref 本体, ref 转场lb, 
                                   false, false, true, false, false, "土神阶段（手动设置）", scriptEnv);
                            return true;

                        case "本体":
                            SetStage(ref 火神, ref 风神, ref 土神, ref 本体, ref 转场lb, 
                                   false, false, false, true, false, "本体阶段（手动设置）", scriptEnv);
                            return true;

                        case "转场LB":
                        case "转场lb":
                            SetStage(ref 火神, ref 风神, ref 土神, ref 本体, ref 转场lb, 
                                   false, false, false, false, true, "转场LB阶段（手动设置）", scriptEnv);
                            return true;

                        case "一运":
                            if (本体)
                            {
                                一运 = true;
                                二运 = false;
                                三运 = false;
                                家长会 = false;
                                SetMainPhaseStage(scriptEnv);
                                LogHelper.Print("一运阶段（手动设置）");
                            }
                            else
                            {
                                LogHelper.PrintError("必须先设置为本体阶段才能设置一运");
                            }
                            return true;

                        case "二运":
                            if (本体)
                            {
                                一运 = false;
                                二运 = true;
                                三运 = false;
                                家长会 = false;
                                SetMainPhaseStage(scriptEnv);
                                LogHelper.Print("二运阶段（手动设置）");
                            }
                            else
                            {
                                LogHelper.PrintError("必须先设置为本体阶段才能设置二运");
                            }
                            return true;

                        case "三运":
                            if (本体)
                            {
                                一运 = false;
                                二运 = false;
                                三运 = true;
                                家长会 = false;
                                SetMainPhaseStage(scriptEnv);
                                LogHelper.Print("三运阶段（手动设置）");
                            }
                            else
                            {
                                LogHelper.PrintError("必须先设置为本体阶段才能设置三运");
                            }
                            return true;

                        case "家长会":
                            if (本体)
                            {
                                一运 = false;
                                二运 = false;
                                三运 = false;
                                家长会 = true;
                                SetMainPhaseStage(scriptEnv);
                                LogHelper.Print("家长会阶段（手动设置）");
                            }
                            else
                            {
                                LogHelper.PrintError("必须先设置为本体阶段才能设置家长会");
                            }
                            return true;

                        default:
                            LogHelper.PrintError($"未知的阶段名称: {stageName}");
                            LogHelper.Print("支持的阶段: 火神、风神、土神、本体、转场LB、一运、二运、三运、家长会");
                            return true;
                    }
                }
                else if (logMessage == "清除阶段")
                {
                    SetStage(ref 火神, ref 风神, ref 土神, ref 本体, ref 转场lb, 
                           false, false, false, false, false, "清除所有阶段状态", scriptEnv);
                    return true;
                }

                return false;
            }
            catch (Exception ex)
            {
                LogHelper.PrintError($"处理阶段设置命令时发生异常: {ex.Message}");
                return false;
            }
        }

        /// <summary>
        /// 处理状态查询命令
        /// 支持的命令：
        /// - "获取当前阶段" - 获取当前主要阶段
        /// - "获取本体细分阶段" - 获取本体阶段的细分
        /// - "获取完整阶段状态" - 获取所有阶段信息
        /// </summary>
        private bool ProcessStatusQuery(string logMessage)
        {
            try
            {
                switch (logMessage)
                {
                    case "获取当前阶段":
                        GetCurrentStage();
                        return true;

                    case "获取本体细分阶段":
                        GetMainPhaseDetail();
                        return true;

                    case "获取完整阶段状态":
                        GetFullStageStatus();
                        return true;

                    default:
                        return false;
                }
            }
            catch (Exception ex)
            {
                LogHelper.PrintError($"处理状态查询时发生异常: {ex.Message}");
                return false;
            }
        }

        /// <summary>
        /// 处理本体阶段技能检测
        /// </summary>
        private void ProcessMainPhaseSkills(uint spellId, ScriptEnv scriptEnv)
        {
            switch (spellId)
                {
                    case 11126: //一运追击
                        一运 = true;
                        二运 = false;
                        三运 = false;
                        家长会 = false;
                        SetMainPhaseStage(scriptEnv);
                    LogHelper.Print("一运追击（技能检测）");
                        break;

                    case 11596: //二运爆击
                        一运 = false;
                        二运 = true;
                        三运 = false;
                        家长会 = false;
                        SetMainPhaseStage(scriptEnv);
                    LogHelper.Print("二运爆击（技能检测）");
                        break;

                    case 11597: //三运乱击
                        一运 = false;
                        二运 = false;
                        三运 = true;
                        家长会 = false;
                        SetMainPhaseStage(scriptEnv);
                    LogHelper.Print("三运乱击（技能检测）");
                        break;

                    case 11147: // 家长会
                        一运 = false;
                        二运 = false;
                        三运 = false;
                        家长会 = true;
                        SetMainPhaseStage(scriptEnv);
                    LogHelper.Print("家长会（技能检测）");
                        break;
            }
        }

        /// <summary>
        /// 设置主要阶段状态
        /// </summary>
        private void SetStage(
            ref bool fireVar, ref bool windVar, ref bool earthVar, ref bool mainVar, ref bool threeLBVar,
            bool fireVal, bool windVal, bool earthVal, bool mainVal, bool threeLBVal,
            string stageName, ScriptEnv scriptEnv)
        {
            fireVar = fireVal;
            windVar = windVal;
            earthVar = earthVal;
            mainVar = mainVal;
            threeLBVar = threeLBVal;

            scriptEnv.KV["火神"] = fireVal;
            scriptEnv.KV["风神"] = windVal;
            scriptEnv.KV["土神"] = earthVal;
            scriptEnv.KV["本体"] = mainVal;
            scriptEnv.KV["转场LB"] = threeLBVal;

            // 当切换到非本体阶段时，清除本体细分阶段
            if (!mainVal)
            {
                一运 = false;
                二运 = false;
                三运 = false;
                家长会 = false;
                ClearMainPhaseStage(scriptEnv);
            }

            if (stageName != null)
            {
                LogHelper.Print(stageName);
            }
        }

        /// <summary>
        /// 设置本体阶段细分状态
        /// </summary>
        private void SetMainPhaseStage(ScriptEnv scriptEnv)
        {
            if (!scriptEnv.KV.ContainsKey("一运"))
            {
                scriptEnv.KV.Add("一运", 一运);
                scriptEnv.KV.Add("二运", 二运);
                scriptEnv.KV.Add("三运", 三运);
                scriptEnv.KV.Add("家长会", 家长会);
            }
            else
            {
                scriptEnv.KV["一运"] = 一运;
                scriptEnv.KV["二运"] = 二运;
                scriptEnv.KV["三运"] = 三运;
                scriptEnv.KV["家长会"] = 家长会;
            }
        }

        /// <summary>
        /// 清除本体阶段细分状态
        /// </summary>
        private void ClearMainPhaseStage(ScriptEnv scriptEnv)
        {
            if (scriptEnv.KV.ContainsKey("一运"))
            {
                scriptEnv.KV["一运"] = false;
                scriptEnv.KV["二运"] = false;
                scriptEnv.KV["三运"] = false;
                scriptEnv.KV["家长会"] = false;
            }
        }

        /// <summary>
        /// 获取当前主要阶段
        /// </summary>
        public void GetCurrentStage()
        {
            List<string> activeStages = new List<string>();

            if (火神) activeStages.Add("火神");
            if (风神) activeStages.Add("风神");
            if (土神) activeStages.Add("土神");
            if (本体) activeStages.Add("本体");
            if (转场lb) activeStages.Add("转场LB");

            if (activeStages.Count == 0)
            {
                LogHelper.Print("当前阶段: 无");
            }
            else
            {
                LogHelper.Print($"当前阶段: {string.Join("、", activeStages)}");
            }
        }

        /// <summary>
        /// 获取本体阶段细分
        /// </summary>
        public void GetMainPhaseDetail()
        {
            if (!本体)
            {
                LogHelper.Print("当前不在本体阶段");
                return;
            }

            List<string> activePhases = new List<string>();

            if (一运) activePhases.Add("一运");
            if (二运) activePhases.Add("二运");
            if (三运) activePhases.Add("三运");
            if (家长会) activePhases.Add("家长会");

            if (activePhases.Count == 0)
            {
                LogHelper.Print("本体阶段细分: 无");
            }
            else
            {
                LogHelper.Print($"本体阶段细分: {string.Join("、", activePhases)}");
            }
        }

        /// <summary>
        /// 获取完整阶段状态
        /// </summary>
        public void GetFullStageStatus()
        {
            LogHelper.Print("=== 完整阶段状态 ===");

            // 主要阶段
            LogHelper.Print($"火神: {(火神 ? "是" : "否")}");
            LogHelper.Print($"风神: {(风神 ? "是" : "否")}");
            LogHelper.Print($"土神: {(土神 ? "是" : "否")}");
            LogHelper.Print($"本体: {(本体 ? "是" : "否")}");
            LogHelper.Print($"转场LB: {(转场lb ? "是" : "否")}");

            // 本体细分阶段
            if (本体)
            {
                LogHelper.Print("--- 本体细分阶段 ---");
                LogHelper.Print($"一运: {(一运 ? "是" : "否")}");
                LogHelper.Print($"二运: {(二运 ? "是" : "否")}");
                LogHelper.Print($"三运: {(三运 ? "是" : "否")}");
                LogHelper.Print($"家长会: {(家长会 ? "是" : "否")}");
            }

            LogHelper.Print("===================");
        }
    }
}

using AEAssist;
using System.Numerics;
using AEAssist.CombatRoutine.Trigger;
using AEAssist.CombatRoutine.Trigger.Node;
using AEAssist.Helper;
using AEAssist.Extension;
using AEAssist.MemoryApi;
using AEAssist.CombatRoutine.Trigger.TriggerCond;
using AEAssist.CombatRoutine.Module;
using AEAssist.Avoid;
using ECommons.GameHelpers;
using System.Linq;
using Dalamud.Game.ClientState.Objects.Types;

namespace AutoRaidHelper.Triggers.UWU.风神
{
    public class T风压 : ITriggerScript
    {
        // 风压处理相关变量
        private Vector3 风圈中心pos;
        private DateTime 初始危险区时间;
        private const int 初始危险区持续时间 = 4000; // 4秒
        private static readonly Random random = new Random();

        // ST专用状态
        private bool ST已处理低气压 = false;
        private bool 风圈已生成 = false;
        private DateTime 最后健康检查时间 = DateTime.MinValue;
        private const int 健康检查间隔 = 1000; // 1秒检查间隔

        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {
            var mapBaseName = "w1fz";
            // 检查风神阶段
            if (scriptEnv.KV.TryGetValue("风神", out var p1))
            {
                if (!(bool)p1)
                {
                    return false;
                }
            }

            // 安全获取当前角色
            var detectedRole = scriptEnv.KV.TryGetValue("DetectedRole", out var roleValue) ? roleValue as string : null;
            bool isST = !string.IsNullOrEmpty(detectedRole) && detectedRole.Equals("ST", StringComparison.OrdinalIgnoreCase);

            // 如果无法获取角色信息，对于非ST角色直接返回true
            if (string.IsNullOrEmpty(detectedRole))
            {
                LogHelper.Print("无法获取角色信息，非ST角色跳过处理");
                return true;
            }

            // 1. 高气压圈读条检测
            if (condParams is EnemyCastSpellCondParams enemyCastSpell)
            {
                if (enemyCastSpell.SpellId == 11078) // 刺羽死亡大暴风
                {
                    风圈中心pos = enemyCastSpell.CastPos;
                    添加风圈危险区(初始危险区持续时间);
                    初始危险区时间 = DateTime.Now;
                    风圈已生成 = true;
                    LogHelper.Print($"高气压圈读条检测，风圈位置：{风圈中心pos}");

                    // ST在风圈生成后开始健康检查
                    if (isST)
                    {
                        最后健康检查时间 = DateTime.Now;
                    }

                    return false;
                }
            }

            // 2. 低气压Buff添加检测
            else if (condParams is AddStatusCondParams addStatus)
            {
                if (addStatus.StatusId == 1525 && addStatus.Target == Core.Me) // 低气压
                {
                    // 获取低气压buff层数
                    int buffStacks = 目标的指定BUFF层数(Core.Me, 1525);

                    // ST的特殊处理逻辑
                    if (isST && !ST已处理低气压)
                    {
                        LogHelper.Print($"ST检测到低气压Buff ({buffStacks}层)，等待风圈生成和团队血量检查");
                        return false;
                    }
                    // 其他职业直接返回true，不处理
                    else if (!isST)
                    {
                        LogHelper.Print($"其他职能检测到低气压Buff ({buffStacks}层)，不处理");
                        return true;
                    }
                    return false;
                }
            }

            // 3. 低气压Buff移除检测
            else if (condParams is RemoveStatusCondParams removeStatus)
            {
                if (removeStatus.StatusId == 1525 && removeStatus.Target == Core.Me)
                {
                    // 只处理ST的buff移除
                    if (isST)
                    {
                        LogHelper.Print("ST低气压Buff移除，停止移动");
                        Core.Resolve<MemApiChatMessage>().Echo("停止移动");
                        ST已处理低气压 = true;
                        LogHelper.Print("ST已完成低气压处理，后续不再进入风圈");
                        return false;
                    }
                    else
                    {
                        // 其他职能直接返回true，不处理
                        LogHelper.Print("其他职能低气压Buff移除，不处理");
                        return true;
                    }
                }
            }

            // 4. ST的定期健康检查
            if (isST && 风圈已生成 && 
                (DateTime.Now - 最后健康检查时间).TotalMilliseconds >= 健康检查间隔)
            {
                最后健康检查时间 = DateTime.Now;

                // 检查ST是否需要处理低气压
                if (!ST已处理低气压 && 检查团队血量健康())
                {
                    LogHelper.Print("ST满足条件，进入风圈处理低气压");
                    移动到风圈中心();
                }
            }

            // 只有ST才会到达这里，其他职能已经在前面返回true
            return false;
        }

        /// <summary>
        /// 检查团队血量是否健康（所有成员血量高于80%）
        /// </summary>
        private bool 检查团队血量健康()
        {
            try
            {
                if (PartyHelper.Party == null || PartyHelper.Party.Count == 0)
                {
                    LogHelper.Print("无法获取队伍信息");
                    return false;
                }

                foreach (var member in PartyHelper.Party)
                {
                    if (member == null) continue;

                    // 计算血量百分比
                    double healthPercentage = (double)member.CurrentHp / member.MaxHp * 100;

                    if (healthPercentage <= 80.0)
                    {
                        LogHelper.Print($"队员血量不足80%: {healthPercentage:F1}%，等待血量恢复");
                        return false;
                    }
                }

                LogHelper.Print("团队血量健康，所有成员血量均高于80%");
                return true;
            }
            catch (Exception ex)
            {
                LogHelper.PrintError($"检查团队血量时发生异常: {ex.Message}");
                return false;
            }
        }

        /// <summary>
        /// 添加风圈危险区
        /// </summary>
        private void 添加风圈危险区(int duration)
        {
            uint randomId = (uint)random.Next(1, int.MaxValue);
            var mapBaseName = "w1fz";

            if (AvoidManager.Instance.GetMap(mapBaseName, out var map))
            {
                map.AddDangerShape(randomId, new DangerShape(0, duration, new CircleShape()
                {
                    Origin = 风圈中心pos,
                    Radius = 7f,
                }));
                LogHelper.Print($"添加风圈危险区，持续时间：{duration}ms");
            }
        }

        /// <summary>
        /// 获取目标的指定BUFF层数
        /// </summary>
        public static int 目标的指定BUFF层数(IBattleChara target, uint buff)
        {
            return Core.Resolve<MemApiBuff>().GetStack(target, buff);
        }

        /// <summary>
        /// 移动到风圈中心
        /// </summary>
        private void 移动到风圈中心()
        {
            // 使用解析移动系统处理移动
            var targetX = 风圈中心pos.X;
            var targetY = 风圈中心pos.Y;
            var targetZ = 风圈中心pos.Z;

            // 通过默语日志触发解析移动系统
            Core.Resolve<MemApiChatMessage>().Echo($"移动到{targetX},{targetY},{targetZ}");

            LogHelper.Print("移动到风圈中心，等待buff消除");
        }
    }
}

