    public class 去安全点 : ITriggerScript
    {
        private DateTime _lastExecutionTime = DateTime.MinValue;

        // 使用事件(True)，则脚本逻辑由Check方法控制。Check返回True代表此节点执行完毕
        public bool Check(ScriptEnv scriptEnv, ITriggerCondParams condParams)
        {
            // 检查是否已过0.1秒


            var mapBaseName = "w1fz";


            if (AvoidManager.Instance.GetMap(mapBaseName, out var map))
            {
                if ((DateTime.Now - _lastExecutionTime).TotalSeconds < 0.1)
                {
                    return false;
                }

                _lastExecutionTime = DateTime.Now;

                if (!map.IsPosSafe(Core.Me.Position))
                {
                    if (map.FindNearestSafePoint(Core.Me.Position, 30, out var safePoint))
                    {
                        Core.Resolve<MemApiMove>().MoveToTarget(safePoint);
                        LogHelper.Print("去安全点");
                    }return false;
                }return false;
            }

            return false;
        }
    }

